import { createReducer, on } from '@ngrx/store';
import { customersAddSuccess, customersGetAllSuccess, customersEditSuccess } from './actions';
const INITIAL_STATE_CUSTOMERS = [];
export const customersReducer = createReducer(INITIAL_STATE_CUSTOMERS, on(customersGetAllSuccess, (state, action) => [...action.payload]), on(customersAddSuccess, (state, action) => [...state, action.payload]), on(customersEditSuccess, (state, action) => [...state.map(product => product._id === action.payload._id ? action.payload : product)]));
export const getCustomers = state => state;
